package Loops;

import java.util.Scanner;

public class EvenNumberInGivenRange {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		 System.out.println(" Even Number in between 121 to 229:");
		 for(int i=121; i<=229; i++)
		{   
			  if(i%2==0)
			   System.out.println(i +" Number is Even");
         }

}
}
