package Loops;

public class OddNumberInGivenRange {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		 System.out.println(" Odd Number in between 521 to 229:");
		 for(int i=521; i>=229; i--)
		 {   
			  if(i%2!=0)
			  {
			   System.out.println(i +" Number is Odd");
			  }
        }

	}

}
